import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { User } from '../App';
import { Button } from './ui/button';
import { Badge } from './ui/badge';
import Toast from './ui/toast';
import {
  LayoutDashboard,
  Bed,
  Users,
  Bell,
  MessageSquare,
  Settings,
  LogOut,
  Sun,
  Moon,
  ChevronLeft,
  ChevronRight,
} from 'lucide-react';

interface LayoutProps {
  children: React.ReactNode;
  currentUser: User | null;
  onLogout: () => void;
}

interface ToastMessage {
  id: string;
  type: 'success' | 'error' | 'info' | 'warning';
  title: string;
  message?: string;
}

const Layout: React.FC<LayoutProps> = ({ children, currentUser, onLogout }) => {
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const [toasts, setToasts] = useState<ToastMessage[]>([]);
  const location = useLocation();

  const addToast = (toast: Omit<ToastMessage, 'id'>) => {
    const id = Math.random().toString(36).substr(2, 9);
    setToasts((prev) => [...prev, { ...toast, id }]);
  };

  const removeToast = (id: string) => {
    setToasts((prev) => prev.filter((toast) => toast.id !== id));
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    document.documentElement.classList.toggle('dark');
    addToast({
      type: 'success',
      title: 'Theme Updated',
      message: `Switched to ${!darkMode ? 'dark' : 'light'} mode`,
    });
  };

  useEffect(() => {
    const handleToastEvent = (event: CustomEvent) => {
      addToast(event.detail);
    };
    window.addEventListener('showToast', handleToastEvent as EventListener);
    return () => {
      window.removeEventListener('showToast', handleToastEvent as EventListener);
    };
  }, []);

  const navigation = [
    { name: 'Dashboard', href: '/dashboard', icon: LayoutDashboard },
    { name: 'Rooms Management', href: '/rooms', icon: Bed },
    { name: 'Guest Management', href: '/guests', icon: Users },
    // { name: 'Notifications', href: '/notifications', icon: Bell },
    { name: 'Feedback & Reviews', href: '/feedback', icon: MessageSquare },
  ];

  // Sidebar width for layout shift
  const sidebarWidth = sidebarCollapsed ? 72 : 260; // px

  return (
    <div
      className={`min-h-screen transition-colors duration-500 flex bg-gradient-to-br from-sky-100 via-white to-sky-50 dark:from-gray-900 dark:via-gray-800 dark:to-black`}
      style={{ minHeight: '100vh' }}
    >
      {/* Sidebar */}
      <aside
        className={`fixed top-0 left-0 h-full z-50 flex flex-col justify-between bg-white dark:bg-slate-900 border-r border-blue-100 dark:border-gray-800 shadow-lg transition-all duration-300 ease-in-out`}
        style={{ width: sidebarWidth, minWidth: sidebarWidth }}
      >
        {/* Logo and Admin Panel Heading */}
        <div className="flex flex-col items-center pt-8 pb-4 px-4 border-b border-blue-100 dark:border-gray-800">
          <div className="flex items-center justify-center w-full">
            <img
              src="/image/hyatt-regency-seeklogo 1.png"
              alt="Hotel Logo"
              className="h-12 w-auto object-contain"
            />
          </div>
          {!sidebarCollapsed && (
            <span className="mt-4 mb-2 block text-lg font-bold text-blue-700 dark:text-blue-200 tracking-wide text-center uppercase">
              Admin Panel
            </span>
          )}
          <Button
            variant="ghost"
            size="icon"
            onClick={() => setSidebarCollapsed((prev) => !prev)}
            className="h-8 w-8 mt-2 rounded-full text-blue-500 dark:text-blue-200 hover:bg-blue-50 dark:hover:bg-slate-800 absolute top-4 right-4"
            aria-label="Toggle sidebar"
            style={{ position: 'absolute', top: 18, right: 18 }}
          >
            {sidebarCollapsed ? <ChevronRight className="h-5 w-5" /> : <ChevronLeft className="h-5 w-5" />}
          </Button>
        </div>

        {/* Navigation */}
        <nav className="flex-1 px-2 py-4 space-y-1 overflow-y-auto">
          {navigation.map((item) => {
            const isActive = location.pathname === item.href;
            return (
              <Link
                key={item.name}
                to={item.href}
                className={`flex items-center gap-4 px-4 py-2 rounded-lg font-medium transition-colors duration-200 group ${
                  isActive
                    ? 'bg-sky-100 text-sky-700 dark:bg-slate-800 dark:text-sky-200 shadow font-semibold'
                    : 'text-blue-900 dark:text-blue-100 hover:bg-sky-50 dark:hover:bg-slate-800 hover:text-sky-700 dark:hover:text-sky-200'
                }`}
                style={{ marginBottom: 2 }}
              >
                <item.icon className={`h-5 w-5 flex-shrink-0 ${isActive ? 'text-sky-600 dark:text-sky-400' : 'text-blue-400 dark:text-blue-300 group-hover:text-sky-600 dark:group-hover:text-sky-400'}`} />
                {!sidebarCollapsed && <span>{item.name}</span>}
              </Link>
            );
          })}
        </nav>

        {/* Divider and Settings/Logout Section */}
        <div className="px-4 pb-6">
          <div className="border-t border-blue-100 dark:border-gray-800 mb-4"></div>
          {/* Settings Button */}
          <Link
            to="/settings"
            className={`flex items-center gap-4 px-4 py-2 rounded-lg font-medium transition-colors duration-200 mb-2 ${
              location.pathname === '/settings'
                ? 'bg-sky-100 text-sky-700 dark:bg-slate-800 dark:text-sky-200 shadow font-semibold'
                : 'text-blue-900 dark:text-blue-100 hover:bg-sky-50 dark:hover:bg-slate-800 hover:text-sky-700 dark:hover:text-sky-200'
            }`}
          >
            <Settings className={`h-5 w-5 flex-shrink-0 ${location.pathname === '/settings' ? 'text-sky-600 dark:text-sky-400' : 'text-blue-400 dark:text-blue-300 group-hover:text-sky-600 dark:group-hover:text-sky-400'}`} />
            {!sidebarCollapsed && <span>Settings</span>}
          </Link>
          {/* Logout Button */}
          <Button
            variant="ghost"
            size="icon"
            onClick={() => {
              onLogout();
              addToast({
                type: 'success',
                title: 'Logged Out',
                message: 'You have been successfully logged out',
              });
            }}
            className="w-full flex items-center gap-4 px-4 py-2 rounded-lg font-medium text-red-600 dark:text-red-400 hover:bg-red-50 dark:hover:bg-red-900/30 mt-1"
            title="Logout"
            aria-label="Logout"
          >
            <LogOut className="h-5 w-5 flex-shrink-0" />
            {!sidebarCollapsed && <span>Logout</span>}
          </Button>
          {/* User Info */}
          {!sidebarCollapsed && (
            <div className="mt-6 flex items-center gap-3 p-3 rounded-lg bg-sky-50 dark:bg-slate-800 border border-blue-100 dark:border-gray-800">
              <div className="w-10 h-10 bg-gradient-to-r from-sky-400 to-blue-500 dark:from-sky-700 dark:to-blue-900 rounded-full flex items-center justify-center shadow-lg">
                <span className="text-white font-bold text-lg">
                  {currentUser?.username?.charAt(0).toUpperCase()}
                </span>
              </div>
              <div className="flex-1 min-w-0 text-blue-900 dark:text-blue-100">
                <p className="text-sm font-semibold truncate">
                  {currentUser?.username}
                </p>
                <p className="text-xs text-blue-500 dark:text-blue-300 truncate">
                  {currentUser?.role}
                </p>
              </div>
            </div>
          )}
        </div>
      </aside>

      {/* Main Content */}
      <div
        className="flex-1 transition-all duration-300"
        style={{ marginLeft: sidebarWidth }}
      >
        {/* Header */}
        <header className="sticky top-0 z-40 bg-white/80 dark:bg-slate-900/80 backdrop-blur-xl border-b border-blue-100 dark:border-gray-800 px-8 py-5 shadow-md">
          <div className="flex items-center justify-between">
            <h1 className="text-3xl font-extrabold bg-gradient-to-r from-sky-600 to-blue-600 bg-clip-text text-transparent drop-shadow-sm">
              {navigation.find((item) => item.href === location.pathname)?.name ||
                'Dashboard'}
            </h1>
            <div className="flex items-center space-x-5">
              {/* Dark Mode Toggle */}
              <Button
                variant="ghost"
                size="icon"
                onClick={toggleDarkMode}
                className="h-10 w-10 rounded-full hover:bg-sky-100 dark:hover:bg-slate-800 transition-all duration-200"
                aria-label="Toggle dark mode"
              >
                {darkMode ? (
                  <Sun className="h-5 w-5 text-yellow-500" />
                ) : (
                  <Moon className="h-5 w-5 text-sky-500" />
                )}
              </Button>
            </div>
          </div>
        </header>
        {/* Page Content */}
        <main className="p-8 space-y-6">
          <div className="bg-white dark:bg-slate-900 shadow-lg rounded-2xl p-6 min-h-[70vh] border border-blue-100 dark:border-gray-800">
            {children}
          </div>
        </main>
      </div>
      {/* Toast Notifications */}
      {toasts.map((toast) => (
        <Toast
          key={toast.id}
          id={toast.id}
          type={toast.type}
          title={toast.title}
          message={toast.message}
          onClose={removeToast}
        />
      ))}
    </div>
  );
};

export default Layout;
