import { useNavigate, Link } from "react-router-dom";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "./ui/card";
import { Button } from "./ui/button";
import {
  Users,
  Bed,
  Bell,
  Calendar,
  Clock,
  Settings,
} from "lucide-react";

const Dashboard = () => {
  const navigate = useNavigate();

  // Mock data for guests
  const guests = [
    {
      id: 1,
      name: "John Doe",
      room: "101",
      checkIn: "2023-05-15",
      checkOut: "2023-05-20",
      status: "checked-in",
    },
    {
      id: 2,
      name: "Jane Smith",
      room: "205",
      checkIn: "2023-05-16",
      checkOut: "2023-05-18",
      status: "checked-in",
    },
    {
      id: 3,
      name: "Robert Johnson",
      room: "312",
      checkIn: "2023-05-17",
      checkOut: "2023-05-19",
      status: "checked-out",
    },
    {
      id: 4,
      name: "Emily Davis",
      room: "104",
      checkIn: "2023-05-18",
      checkOut: "2023-05-22",
      status: "checked-in",
    },
    {
      id: 5,
      name: "Michael Brown",
      room: "201",
      checkIn: "2023-05-19",
      checkOut: "2023-05-21",
      status: "checked-in",
    },
  ];

  // Mock data for rooms
  const rooms = [
    { number: 101, status: 'available', type: 'Deluxe' },
    { number: 102, status: 'occupied', type: 'Standard' },
    { number: 103, status: 'available', type: 'Suite' },
    { number: 104, status: 'maintenance', type: 'Presidential' },
    { number: 105, status: 'available', type: 'Standard' },
    { number: 106, status: 'occupied', type: 'Deluxe' },
    { number: 107, status: 'available', type: 'Suite' },
    { number: 108, status: 'available', type: 'Standard' },
    { number: 109, status: 'occupied', type: 'Presidential' },
    { number: 110, status: 'available', type: 'Deluxe' },
    { number: 201, status: 'maintenance', type: 'Standard' },
    { number: 202, status: 'available', type: 'Suite' },
    { number: 203, status: 'occupied', type: 'Deluxe' },
    { number: 204, status: 'available', type: 'Standard' },
    { number: 205, status: 'occupied', type: 'Presidential' },
    { number: 206, status: 'available', type: 'Deluxe' },
    { number: 207, status: 'maintenance', type: 'Standard' },
    { number: 208, status: 'available', type: 'Suite' },
    { number: 209, status: 'occupied', type: 'Deluxe' },
    { number: 210, status: 'available', type: 'Standard' },
    { number: 301, status: 'available', type: 'Presidential' },
    { number: 302, status: 'occupied', type: 'Deluxe' },
    { number: 303, status: 'available', type: 'Standard' },
    { number: 304, status: 'maintenance', type: 'Suite' },
    { number: 305, status: 'available', type: 'Deluxe' },
  ];

  const notifications = [
    {
      id: 1,
      type: "checkin",
      message: "New guest checked in to Room 205",
      time: "2 minutes ago",
    },
    {
      id: 2,
      type: "maintenance",
      message: "Maintenance request for Room 312",
      time: "15 minutes ago",
    },
    {
      id: 3,
      type: "checkout",
      message: "Guest checked out from Room 108",
      time: "1 hour ago",
    },
    {
      id: 4,
      type: "review",
      message: "New 5-star review received",
      time: "2 hours ago",
    },
  ];

  // Live Room Status mock data
  const liveRoomStatus = [
    { room: '101', guest: 'Guest', mode: 'Idle', lastAction: '', tabletStatus: 'Online' },
    { room: '102', guest: 'Jane Smith', mode: 'Do Not Disturb', lastAction: '11:10 AM', tabletStatus: 'Online' },
    { room: '103', guest: 'John Doe', mode: 'Cleaning', lastAction: '', tabletStatus: 'Offline' },
    { room: '104', guest: 'Guest', mode: 'Idle', lastAction: '', tabletStatus: 'Online' },
    { room: '105', guest: 'Alice Brown', mode: 'Occupied', lastAction: '10:45 AM', tabletStatus: 'Online' },
    { room: '106', guest: 'Bob White', mode: 'Idle', lastAction: '', tabletStatus: 'Offline' },
    { room: '107', guest: 'Charlie Black', mode: 'Do Not Disturb', lastAction: '09:30 AM', tabletStatus: 'Online' },
    { room: '108', guest: 'Diana Green', mode: 'Cleaning', lastAction: '', tabletStatus: 'Online' },
  ];

  // Dashboard summary cards data (matching the image)
  const dashboardStats = [
    { value: '135', label: 'Total Rooms', href: '/rooms' },
    { value: '6', label: 'Clean Requests', href: '/clean-requests' },
    { value: '3', label: 'Technical Issues', href: '/technical-issues' },
    { value: '4.2', label: 'Guest Feedback', href: '/feedback' },
  ];

  const getNotificationIcon = (type: string) => {
    switch (type) {
      case "checkin":
      case "checkout":
        return <Users className="h-4 w-4" />;
      case "maintenance":
        return <Bed className="h-4 w-4" />;
      case "review":
        return <Bell className="h-4 w-4" />;
      default:
        return <Bell className="h-4 w-4" />;
    }
  };

  // Mobile-friendly table row component
  interface RoomData {
    room: string;
    guest: string;
    mode: string;
    lastAction: string;
    tabletStatus: string;
  }

  const MobileTableRow = ({ room }: { room: RoomData }) => (
    <div className="bg-white dark:bg-slate-800 rounded-lg shadow-sm p-3 mb-2 border border-gray-100 dark:border-slate-700">
      <div className="grid grid-cols-2 gap-2 text-sm">
        <div className="font-medium">Room</div>
        <div>{room.room}</div>

        <div className="font-medium">Guest</div>
        <div className="truncate">{room.guest}</div>

        <div className="font-medium">Status</div>
        <div>
          <span className={`px-2 py-0.5 text-xs font-medium rounded-full ${
            room.tabletStatus === 'Online' 
              ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200' 
              : 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200'
          }`}>
            {room.tabletStatus}
          </span>
        </div>

        <div className="font-medium">Mode</div>
        <div>{room.mode}</div>

        {room.lastAction && (
          <>
            <div className="font-medium">Last Action</div>
            <div>{room.lastAction}</div>
          </>
        )}
      </div>
    </div>
  );

  return (
    <div className="space-y-4 p-3 sm:p-4 md:p-6 bg-gradient-to-br from-slate-50 to-slate-100 dark:from-slate-900 dark:to-slate-800 min-h-screen">
      {/* Header */}

        {/* <div className="flex items-center space-x-3">
          <Button
            size="sm"
            className="bg-gradient-to-r from-blue-500 to-purple-500 hover:from-blue-600 hover:to-purple-600 transition-all duration-200"
          >
            View Details
          </Button>
        </div> */}

      {/* Live Room Status Section - Mobile */}
      <div className="block md:hidden">
        <Card className="border-0 bg-white/80 dark:bg-slate-800/80 backdrop-blur-sm">
          <CardHeader className="pb-3">
            <div>
              <CardTitle className="text-lg font-semibold">
                Live Room Status
              </CardTitle>
              <CardDescription className="text-sm">Current status of all rooms</CardDescription>
            </div>
          </CardHeader>
          <CardContent className="px-3 py-2">
            <div className="space-y-2 max-h-96 overflow-y-auto">
              {liveRoomStatus.map((room, idx) => (
                <MobileTableRow key={idx} room={room} />
              ))}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Live Room Status Section - Desktop */}
      <div className="hidden md:block">
        <Card className="border-0 bg-white/80 dark:bg-slate-800/80 backdrop-blur-sm">
          <CardHeader className="pb-3">
            <div className="flex justify-between items-center">
              <div>
                <CardTitle className="text-xl font-semibold">
                  Live Room Status
                </CardTitle>
                <CardDescription>Current status of all rooms</CardDescription>
              </div>
            </div>
          </CardHeader>
          <CardContent>
            <div className="overflow-x-auto">
              <div className="max-h-96 overflow-y-auto text-sm">
                <table className="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
                  <thead className="bg-gray-50 dark:bg-slate-700/50 sticky top-0 z-10">
                    <tr>
                      <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Room</th>
                      <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Guest</th>
                      <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Mode</th>
                      <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Last Action</th>
                      <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Status</th>
                    </tr>
                  </thead>
                  <tbody className="bg-white dark:bg-slate-800 divide-y divide-gray-200 dark:divide-gray-700">
                    {liveRoomStatus.map((room, idx) => (
                      <tr key={idx} className="hover:bg-gray-50 dark:hover:bg-slate-700/50">
                        <td className="px-4 py-3 text-sm font-medium text-gray-900 dark:text-white">{room.room}</td>
                        <td className="px-4 py-3 text-sm text-gray-500 dark:text-gray-300">{room.guest}</td>
                        <td className="px-4 py-3 text-sm text-gray-500 dark:text-gray-300">{room.mode}</td>
                        <td className="px-4 py-3 text-sm text-gray-500 dark:text-gray-300">{room.lastAction || '—'}</td>
                        <td className="px-4 py-3">
                          <span className={`px-2 py-1 inline-flex text-xs leading-4 font-semibold rounded-full ${
                            room.tabletStatus === 'Online' 
                              ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200' 
                              : 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200'
                          }`}>
                            {room.tabletStatus}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                {liveRoomStatus.length > 4 && (
                  <div className="text-xs text-gray-400 dark:text-gray-500 text-center py-2">Scroll to see more rooms...</div>
                )}
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Dashboard Summary Cards */}
      <div className="grid grid-cols-2 gap-3 my-4">
        {dashboardStats.map((stat, idx) => (
          <Card 
            key={idx} 
            onClick={() => navigate(stat.href)} 
            className="cursor-pointer flex flex-col items-center justify-center p-3 bg-white dark:bg-slate-800 rounded-lg shadow-sm border border-gray-100 dark:border-gray-700 hover:shadow-md transition-shadow"
          >
            <span className="text-2xl font-bold text-gray-900 dark:text-white mb-1">{stat.value}</span>
            <span className="text-xs text-center text-gray-500 dark:text-gray-300">{stat.label}</span>
          </Card>
        ))}
      </div>

      {/* Quick Actions Grid */}
      <div className="grid grid-cols-2 gap-3 mb-6">
        {[
          { 
            icon: <span className="text-lg">+</span>, 
            label: 'Add Room', 
            onClick: () => navigate('/rooms') 
          },
          { 
            icon: <Users className="h-5 w-5" />, 
            label: 'Assign Guest', 
            onClick: () => navigate('/guests') 
          },
          { 
            icon: <Bell className="h-5 w-5" />, 
            label: 'Notifications', 
            onClick: () => navigate('/notifications') 
          },
          { 
            icon: <Bell className="h-5 w-5" />, 
            label: 'Clean', 
            onClick: () => navigate('/clean-requests') 
          },
          { 
            icon: <Settings className="h-5 w-5" />, 
            label: 'Issues', 
            onClick: () => navigate('/technical-issues') 
          },
          { 
            icon: <Settings className="h-5 w-5" />, 
            label: 'Display', 
            onClick: () => navigate('/configure-display') 
          },
        ].map((action, idx) => (
          <Button
            key={idx}
            onClick={action.onClick}
            className="h-14 flex flex-col items-center justify-center p-2 text-xs font-medium text-center bg-white dark:bg-slate-800 hover:bg-gray-50 dark:hover:bg-slate-700 border border-gray-200 dark:border-gray-700 rounded-lg"
            variant="ghost"
          >
            <div className="mb-1">{action.icon}</div>
            <span className="text-xs whitespace-nowrap">{action.label}</span>
          </Button>
        ))}
      </div>

      {/* Notifications Section */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-4 mt-2">
        <Card className="lg:col-span-2 border-0 bg-white/80 dark:bg-slate-800/80 backdrop-blur-sm hover:shadow-xl transition-all duration-300">
          <CardHeader className="pb-4">
            <CardTitle className="text-xl font-semibold">
              Recent Activity
            </CardTitle>
            <CardDescription className="text-base">
              Latest hotel activities and updates
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-3 sm:space-y-4 md:space-y-6">
              {notifications.map((notification) => (
                <div
                  key={notification.id}
                  className="flex items-start space-x-3 p-3 rounded-lg border border-gray-100 dark:border-gray-700 hover:bg-gray-50 dark:hover:bg-slate-700/50 transition-colors"
                >
                  <div className="flex-shrink-0 p-1.5 rounded-md bg-blue-100 dark:bg-blue-900/30 flex items-center justify-center">
                    {getNotificationIcon(notification.type)}
                  </div>
                  <div className="flex-1 min-w-0">
                    <p className="text-sm font-medium text-foreground leading-relaxed break-words">
                      {notification.message}
                    </p>
                    <div className="flex items-center space-x-2 mt-1">
                      <div className="flex items-center space-x-1">
                        <Clock className="h-3 w-3 text-muted-foreground" />
                        <span className="text-xs text-muted-foreground">
                          {notification.time}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Quick Actions */}
        <Card className="border-0 bg-white/80 dark:bg-slate-800/80 backdrop-blur-sm hover:shadow-xl transition-all duration-300">
          <CardHeader className="pb-4">
