                  </div>
                </div>

                <div className="mt-4">
                  <p className="text-sm text-slate-400 mb-2">Amenities</p>
                  <div className="flex flex-wrap gap-2">
                    {mockRoom.amenities.map((a) => (
                      <span key={a} className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-slate-800/60">
                        {a === "wifi" && <Wifi className="h-3 w-3 mr-1" />}
                        {a === "coffee" && <Coffee className="h-3 w-3 mr-1" />}
                        {a === "gym" && <Dumbbell className="h-3 w-3 mr-1" />}
                        {a === "parking" && <Car className="h-3 w-3 mr-1" />}
                        {a}
                      </span>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Right column (thermostat, quick actions, booking) */}
          <div className="space-y-6">
            <div className="glass-card rounded-2xl p-6 flex flex-col items-center gap-4">
              <div className="thermo-ring">
                {/* SVG arc built by conic angle based on temperature (we set static) */}
                <div
                  className="arc"
                  style={{
                    background: `conic-gradient(#7c3aed ${((currentTemp - 16) / (32 - 16)) * 360}deg, rgba(255,255,255,0.03) 0deg)`,
                  }}
                />
                <div className="inner">
                  <Thermometer className="h-6 w-6 text-slate-300 mb-2" />
                  <div className="text-4xl font-bold">{currentTemp}</div>
                  <div className="text-sm text-slate-400">Â°C</div>
                </div>
              </div>

              <div className="w-full text-center">
                <div className="text-sm text-slate-400 mb-2">Thermostat</div>
                <div className="flex items-center gap-2 justify-center">
                  <button
                    className="px-3 py-2 rounded-lg bg-slate-800 text-sm"
                    onClick={() => setCurrentTemp((t) => Math.max(16, t - 1))}
                  >
                    -
                  </button>
                  <button
                    className="px-4 py-2 rounded-lg bg-violet-600 text-sm font-medium"
                    onClick={() => setCurrentTemp((t) => Math.min(32, t + 1))}
                  >
                    Set
                  </button>
                  <button
                    className="px-3 py-2 rounded-lg bg-slate-800 text-sm"
                    onClick={() => setCurrentTemp((t) => Math.min(32, t + 1))}
                  >
                    +
                  </button>
                </div>
              </div>
            </div>

            <div className="glass-card rounded-2xl p-4">
              <div className="flex items-center justify-between mb-3">
                <h3 className="font-semibold">Quick Actions</h3>
              </div>
              <div className="grid grid-cols-3 gap-3">
                <button className="flex flex-col items-center py-4 rounded-lg bg-slate-800/60">
                  <Phone className="h-5 w-5 mb-2 text-slate-200" />
                  <div className="text-xs text-slate-300">Call</div>
                </button>
                <button className="flex flex-col items-center py-4 rounded-lg bg-slate-800/60">
                  <Mail className="h-5 w-5 mb-2 text-slate-200" />
                  <div className="text-xs text-slate-300">Mail</div>
                </button>
                <button className="col-span-3 py-3 rounded-lg bg-slate-800/60 flex items-center justify-center gap-2">
                  <div className="p-2 rounded-full bg-slate-700/50">
                    <Edit className="h-4 w-4" />
                  </div>
                  <div className="text-sm">Report Technical Issue</div>
                </button>
              </div>
            </div>

            {(mockRoom.checkIn || mockRoom.checkOut) && (
              <div className="glass-card rounded-2xl p-4">
                <h3 className="font-semibold mb-3">Booking Information</h3>
                <div className="grid grid-cols-3 gap-3 text-sm">
                  <div className="flex items-center gap-3">
                    <Calendar className="h-5 w-5 text-slate-400" />
                    <div>
                      <p className="text-xs text-slate-400">Check-in</p>
                      <p className="font-medium">{new Date(mockRoom.checkIn).toLocaleString()}</p>
                    </div>
                  </div>
                  <div className="flex items-center gap-3">
                    <Calendar className="h-5 w-5 text-slate-400" />
                    <div>
                      <p className="text-xs text-slate-400">Check-out</p>
                      <p className="font-medium">{new Date(mockRoom.checkOut).toLocaleString()}</p>
                    </div>
                  </div>
                  <div className="flex items-center gap-3">
                    <Clock className="h-5 w-5 text-slate-400" />
                    <div>
                      <p className="text-xs text-slate-400">Duration</p>
                      <p className="font-medium">{calcNights(mockRoom.checkIn, mockRoom.checkOut)}</p>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

interface RoomDetailsModalProps {
  isOpen: boolean;
  onClose: () => void;
  room: {
    id: string;
    number: string;
    type: string;
    floor: number;
    status: 'available' | 'occupied' | 'maintenance' | 'cleaning';
    guestName?: string;
    phone?: string;
    email?: string;
    notes?: string;
    checkIn?: string;
    checkOut?: string;
    amenities: string[];
    lastUpdated: string;
  };
}

export const RoomDetailsModal: React.FC<RoomDetailsModalProps> = ({ isOpen, onClose, room }) => {
  const [isDarkMode, setIsDarkMode] = useState(false);

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4" onClick={onClose}>
      <div 
        className={`${isDarkMode ? 'dark' : ''} bg-white dark:bg-slate-900 rounded-2xl w-full max-w-5xl max-h-[90vh] overflow-y-auto`}
        onClick={(e) => e.stopPropagation()}
      >
        <div className="relative">
          <button 
            onClick={onClose}
            className="absolute right-4 top-4 p-2 rounded-full hover:bg-gray-100 dark:hover:bg-gray-800 z-10"
          >
            <X className="h-5 w-5 text-gray-500 dark:text-gray-400" />
          </button>
          <button 
            onClick={() => setIsDarkMode(!isDarkMode)}
            className="absolute right-16 top-4 p-2 rounded-full hover:bg-gray-100 dark:hover:bg-gray-800 z-10"
          >
            {isDarkMode ? (
              <Sun className="h-5 w-5 text-yellow-400" />
            ) : (
              <Moon className="h-5 w-5 text-gray-600" />
            )}
          </button>
          <HotelDashboard />
        </div>
      </div>
    </div>
  );
};

/* Small helper icon (green check) to keep the file self-contained */
function CheckIconSmall() {
  return (
    <svg width="18" height="18" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
      <path d="M20 6L9 17L4 12" stroke="#10B981" strokeWidth="2.2" strokeLinecap="round" strokeLinejoin="round" />
    </svg>
  );
}
