import React, { useState } from 'react';
import {
  X,
  Thermometer,
  Clock,
  Calendar,
  Sun,
  Wifi,
  Tv,
  Bell,
  Bed,
  Phone,
  Mail,
  Edit,
  Trash2,
  Users as UsersIcon,
  Coffee,
  Dumbbell,
  Car
} from 'lucide-react';
import { Button } from './ui/button';
import { Dialog, DialogContent } from './ui/dialog';

interface RoomDetailsModalProps {
  isOpen: boolean;
  onClose: () => void;
  room: {
    id: string;
    number: string;
    type: string;
    floor: number;
    status: 'available' | 'occupied' | 'maintenance' | 'cleaning';
    guestName?: string;
    phone?: string;
    email?: string;
    notes?: string;
    checkIn?: string;
    checkOut?: string;
    amenities: string[];
    lastUpdated: string;
  };
}

const RoomDetailsModal: React.FC<RoomDetailsModalProps> = ({ isOpen, onClose, room }) => {
  const [doNotDisturb, setDoNotDisturb] = useState(false);
  const [lightningControl, setLightningControl] = useState('Auto');
  const [currentTemp, setCurrentTemp] = useState(22);

  // Current time and date
  const now = new Date();
  const timeString = now.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' });
  const dateString = now.toLocaleDateString('en-US', {
    weekday: 'short',
    day: '2-digit',
    month: 'short',
    year: 'numeric'
  });

  const statusColors = {
    available: 'bg-green-50 text-green-700 border border-green-100',
    occupied: 'bg-red-50 text-red-700 border border-red-100',
    maintenance: 'bg-yellow-50 text-yellow-700 border border-yellow-100',
    cleaning: 'bg-blue-50 text-blue-700 border border-blue-100'
  };

  const statusText = {
    available: 'Available',
    occupied: 'Occupied',
    maintenance: 'Maintenance',
    cleaning: 'Cleaning'
  };

  const formatDate = (dateString?: string) => {
    if (!dateString) return 'N/A';
    const options: Intl.DateTimeFormatOptions = {
      weekday: 'short',
      day: '2-digit',
      month: 'short',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    return new Date(dateString).toLocaleString('en-US', options);
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-5xl p-0 overflow-hidden max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="p-6 border-b">
          <div className="flex justify-between items-center">
            <div className="flex items-center gap-4">
              <div className="p-3 bg-blue-50 rounded-xl">
                <Thermometer className="h-8 w-8 text-blue-600" />
              </div>
              <div>
                <h2 className="text-3xl font-bold">{currentTemp}°C</h2>
                <p className="text-sm text-gray-500">Temperature</p>
              </div>
            </div>
            <div className="text-right">
              <div className="flex items-center gap-2 justify-end">
                <Clock className="h-5 w-5 text-gray-500" />
                <span className="text-lg font-medium">{timeString}</span>
              </div>
              <p className="text-sm text-gray-500">{dateString}</p>
            </div>
          </div>
        </div>

        <div className="p-6 space-y-6">
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            {/* Left Column */}
            <div className="lg:col-span-2 space-y-6">
              {/* Room Image */}
              <div className="bg-gray-100 rounded-xl h-48 flex items-center justify-center">
                <Bed className="h-16 w-16 text-gray-400" />
                <span className="sr-only">Room {room.number} image</span>
              </div>

              {/* Room Controls */}
              <div className="bg-white rounded-xl border p-4">
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg font-semibold">Room Controls</h3>
                  <div className="flex items-center space-x-2">
                    <span className="text-xs text-gray-500">All Devices</span>
                    <div className="relative inline-block w-10 mr-2 align-middle select-none">
                      <input type="checkbox" id="toggle-all" className="toggle-checkbox absolute block w-5 h-5 rounded-full bg-white border-4 appearance-none cursor-pointer" />
                      <label htmlFor="toggle-all" className="toggle-label block overflow-hidden h-5 rounded-full bg-gray-300 cursor-pointer"></label>
                    </div>
                  </div>
                </div>
                <div className="grid grid-cols-2 sm:grid-cols-4 gap-3">
                  {[
                    { icon: Sun, label: 'Lights', active: true },
                    { icon: Thermometer, label: 'AC', active: false },
                    { icon: Wifi, label: 'WiFi', active: true },
                    { icon: Tv, label: 'TV', active: false }
                  ].map(({ icon: Icon, label, active }) => (
                    <div key={label} className="flex flex-col items-center">
                      <button
                        className={`w-14 h-14 rounded-xl flex items-center justify-center mb-1.5 ${
                          active ? 'bg-blue-100' : 'bg-gray-100'
                        }`}
                      >
                        <Icon className={`h-6 w-6 ${active ? 'text-blue-600' : 'text-gray-500'}`} />
                      </button>
                      <span className="text-xs font-medium">{label}</span>
                    </div>
                  ))}
                </div>
              </div>

              {/* Guest Information */}
              {room.guestName && (
                <div className="bg-white rounded-xl border p-4">
                  <div className="flex justify-between items-center mb-4">
                    <h3 className="text-lg font-semibold">Guest Information</h3>
                    <Button variant="outline" size="sm" className="text-xs h-8">
                      <Edit className="h-3.5 w-3.5 mr-1.5" />
                      Edit
                    </Button>
                  </div>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div className="space-y-1">
                      <p className="text-xs font-medium text-gray-500">GUEST NAME</p>
                      <p className="text-sm font-medium">{room.guestName}</p>
                    </div>
                    <div className="space-y-1">
                      <p className="text-xs font-medium text-gray-500">PHONE</p>
                      <p className="text-sm font-medium flex items-center">
                        <Phone className="h-3.5 w-3.5 mr-2 text-gray-500" />
                        {room.phone || 'N/A'}
                      </p>
                    </div>
                    <div className="space-y-1">
                      <p className="text-xs font-medium text-gray-500">EMAIL</p>
                      <p className="text-sm font-medium flex items-center">
                        <Mail className="h-3.5 w-3.5 mr-2 text-gray-500" />
                        {room.email || 'N/A'}
                      </p>
                    </div>
                    <div className="space-y-1">
                      <p className="text-xs font-medium text-gray-500">NOTES</p>
                      <p className="text-sm font-medium">{room.notes || 'No special requests'}</p>
                    </div>
                  </div>
                </div>
              )}

              {/* Room Details */}
              <div className="bg-white rounded-xl border p-4">
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg font-semibold">Room Details</h3>
                  <span className={`px-3 py-1 rounded-full text-xs font-medium ${statusColors[room.status]}`}>
                    {statusText[room.status]}
                  </span>
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-1">
                    <p className="text-sm text-gray-500">Room No.</p>
                    <p className="font-medium">{room.number}</p>
                  </div>
                  <div className="space-y-1">
                    <p className="text-sm text-gray-500">Type</p>
                    <p className="font-medium">{room.type}</p>
                  </div>
                  <div className="space-y-1">
                    <p className="text-sm text-gray-500">Floor</p>
                    <p className="font-medium">Floor {room.floor}</p>
                  </div>
                </div>

                {/* Amenities */}
                <div className="mt-4">
                  <p className="text-sm text-gray-500 mb-2">Amenities</p>
                  <div className="flex flex-wrap gap-2">
                    {room.amenities.map((amenity, index) => (
                      <span
                        key={index}
                        className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-gray-100 text-gray-800"
                      >
                        {amenity === 'wifi' && <Wifi className="h-3 w-3 mr-1" />}
                        {amenity === 'coffee' && <Coffee className="h-3 w-3 mr-1" />}
                        {amenity === 'gym' && <Dumbbell className="h-3 w-3 mr-1" />}
                        {amenity === 'parking' && <Car className="h-3 w-3 mr-1" />}
                        {amenity}
                      </span>
                    ))}
                  </div>
                </div>
              </div>
            </div>

            {/* Right Column */}
            <div className="space-y-4">
              {/* Do Not Disturb */}
              <div className="bg-white rounded-xl border p-4">
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <Bell className="h-5 w-5 text-gray-600 mr-3" />
                    <div>
                      <h3 className="font-medium">Do Not Disturb</h3>
                      <p className="text-sm text-gray-500">Silence all notifications</p>
                    </div>
                  </div>
                  <button
                    onClick={() => setDoNotDisturb(!doNotDisturb)}
                    className={`w-12 h-6 rounded-full p-1 transition-colors ${
                      doNotDisturb ? 'bg-blue-600' : 'bg-gray-200'
                    }`}
                  >
                    <div
                      className={`bg-white w-4 h-4 rounded-full shadow-md transform transition-transform ${
                        doNotDisturb ? 'translate-x-6' : 'translate-x-0'
                      }`}
                    />
                  </button>
                </div>
              </div>

              {/* Lightning Control */}
              <div className="bg-white rounded-xl border p-4">
                <div className="flex items-center justify-between mb-3">
                  <h3 className="font-medium">Lightning Control</h3>
                  <span className="text-sm text-blue-600">{lightningControl}</span>
                </div>
                <div className="grid grid-cols-3 gap-2">
                  {['Dim', 'Auto', 'Bright'].map((mode) => (
                    <button
                      key={mode}
                      onClick={() => setLightningControl(mode)}
                      className={`py-2 px-3 rounded-lg text-sm font-medium ${
                        lightningControl === mode
                          ? 'bg-blue-100 text-blue-700'
                          : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                      }`}
                    >
                      {mode}
                    </button>
                  ))}
                </div>
              </div>

              {/* Quick Actions */}
              <div className="bg-white rounded-xl border p-4">
                <h3 className="font-medium mb-3">Quick Actions</h3>
                <div className="space-y-2">
                  <Button variant="outline" className="w-full justify-start">
                    <Edit className="h-4 w-4 mr-2" />
                    Edit Room
                  </Button>
                  <Button variant="outline" className="w-full justify-start">
                    <UsersIcon className="h-4 w-4 mr-2" />
                    Assign Guest
                  </Button>
                  <Button
                    variant="outline"
                    className="w-full justify-start text-red-600 hover:text-red-700"
                  >
                    <Trash2 className="h-4 w-4 mr-2" />
                    Delete Room
                  </Button>
                </div>
              </div>

              {/* Booking Info */}
              {(room.checkIn || room.checkOut) && (
                <div className="bg-white rounded-xl border p-4">
                  <h3 className="text-lg font-semibold mb-4">Booking Information</h3>
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <div className="flex items-center">
                      <Calendar className="h-5 w-5 text-gray-500 mr-3" />
                      <div>
                        <p className="text-sm text-gray-500">Check-in</p>
                        <p className="font-medium">{formatDate(room.checkIn)}</p>
                      </div>
                    </div>
                    <div className="flex items-center">
                      <Calendar className="h-5 w-5 text-gray-500 mr-3" />
                      <div>
                        <p className="text-sm text-gray-500">Check-out</p>
                        <p className="font-medium">{formatDate(room.checkOut)}</p>
                      </div>
                    </div>
                    <div className="flex items-center">
                      <Clock className="h-5 w-5 text-gray-500 mr-3" />
                      <div>
                        <p className="text-sm text-gray-500">Duration</p>
                        <p className="font-medium">
                          {room.checkIn && room.checkOut
                            ? `${Math.ceil(
                                (new Date(room.checkOut).getTime() -
                                  new Date(room.checkIn).getTime()) /
                                  (1000 * 60 * 60 * 24)
                              )} nights`
                            : 'N/A'}
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
};

export default RoomDetailsModal;
