import { useNavigate } from "react-router-dom";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "./ui/card";
import { Button } from "./ui/button";
import {
  Users,
  Bed,
  Bell,
  Calendar,
  Clock,
} from "lucide-react";

const Dashboard = () => {
  const navigate = useNavigate();

  // Mock data for guests
  const guests = [
    {
      id: 1,
      name: "John Doe",
      room: "101",
      checkIn: "2023-05-15",
      checkOut: "2023-05-20",
      status: "checked-in",
    },
    {
      id: 2,
      name: "Jane Smith",
      room: "205",
      checkIn: "2023-05-16",
      checkOut: "2023-05-18",
      status: "checked-in",
    },
    {
      id: 3,
      name: "Robert Johnson",
      room: "312",
      checkIn: "2023-05-17",
      checkOut: "2023-05-19",
      status: "checked-out",
    },
    {
      id: 4,
      name: "Emily Davis",
      room: "104",
      checkIn: "2023-05-18",
      checkOut: "2023-05-22",
      status: "checked-in",
    },
    {
      id: 5,
      name: "Michael Brown",
      room: "201",
      checkIn: "2023-05-19",
      checkOut: "2023-05-21",
      status: "checked-in",
    },
  ];

  // Mock data for rooms
  const rooms = Array.from({ length: 20 }, (_, i) => ({
    number: 100 + i + 1,
    status: ["available", "occupied", "maintenance"][
      Math.floor(Math.random() * 3)
    ],
    type: ["Standard", "Deluxe", "Suite", "Presidential"][
      Math.floor(Math.random() * 4)
    ],
  }));

  const notifications = [
    {
      id: 1,
      type: "checkin",
      message: "New guest checked in to Room 205",
      time: "2 minutes ago",
    },
    {
      id: 2,
      type: "maintenance",
      message: "Maintenance request for Room 312",
      time: "15 minutes ago",
    },
    {
      id: 3,
      type: "checkout",
      message: "Guest checked out from Room 108",
      time: "1 hour ago",
    },
    {
      id: 4,
      type: "review",
      message: "New 5-star review received",
      time: "2 hours ago",
    },
  ];

  const stats = [
    {
      title: "Total Rooms",
      value: "150",
      icon: Bed,
      gradient: "from-blue-500 to-blue-600",
    },
    {
      title: "Occupied Rooms",
      value: "127",
      icon: Users,
      gradient: "from-green-500 to-green-600",
    },
    {
      title: "Available Rooms",
      value: "23",
      icon: Bed,
      gradient: "from-purple-500 to-purple-600",
    },
    {
      title: "Guests",
      value: "89",
      icon: Users,
      gradient: "from-pink-500 to-pink-600",
    },
  ];

  const getNotificationIcon = (type: string) => {
    switch (type) {
      case "checkin":
      case "checkout":
        return <Users className="h-4 w-4" />;
      case "maintenance":
        return <Bed className="h-4 w-4" />;
      case "review":
        return <Bell className="h-4 w-4" />;
      default:
        return <Bell className="h-4 w-4" />;
    }
  };

  return (
    <div className="space-y-8 p-6 bg-gradient-to-br from-slate-50 to-slate-100 dark:from-slate-900 dark:to-slate-800 min-h-screen">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-4xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
            Dashboard
          </h2>
          <p className="text-muted-foreground mt-2 text-lg">
            Welcome back! Here's what's happening today.
          </p>
        </div>
        <div className="flex items-center space-x-3">
          <Button
            size="sm"
            className="bg-gradient-to-r from-blue-500 to-purple-500 hover:from-blue-600 hover:to-purple-600 transition-all duration-200"
          >
            View Details
          </Button>
        </div>
      </div>

      {/* Guest List Section */}
      <div className="grid grid-cols-1 gap-8">
        <Card className="border-0 bg-white/80 dark:bg-slate-800/80 backdrop-blur-sm hover:shadow-xl transition-all duration-300">
          <CardHeader className="pb-4">
            <div className="flex justify-between items-center">
              <div>
                <CardTitle className="text-xl font-semibold">
                  Guest List
                </CardTitle>
                <CardDescription>Current and upcoming guests</CardDescription>
              </div>
              <Button
                onClick={() => navigate("/guests")}
                variant="outline"
                className="bg-gradient-to-r from-blue-500 to-purple-500 text-white hover:from-blue-600 hover:to-purple-600"
              >
                View All Guests
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
                <thead className="bg-gray-50 dark:bg-slate-700/50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                      Name
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                      Room
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                      Check-in
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                      Check-out
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                      Status
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white dark:bg-slate-800 divide-y divide-gray-200 dark:divide-gray-700">
                  {guests.map((guest) => (
                    <tr
                      key={guest.id}
                      className="hover:bg-gray-50 dark:hover:bg-slate-700/50"
                    >
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 dark:text-white">
                        {guest.name}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300">
                        {guest.room}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300">
                        {guest.checkIn}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300">
                        {guest.checkOut}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span
                          className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                            guest.status === "checked-in"
                              ? "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200"
                              : "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200"
                          }`}
                        >
                          {guest.status}
                        </span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Rooms Overview */}
      <div className="grid grid-cols-1 gap-8">
        <Card className="border-0 bg-white/80 dark:bg-slate-800/80 backdrop-blur-sm hover:shadow-xl transition-all duration-300">
          <CardHeader className="pb-4">
            <div className="flex justify-between items-center">
              <div>
                <CardTitle className="text-xl font-semibold">
                  Rooms Overview
                </CardTitle>
                <CardDescription>Room status at a glance</CardDescription>
              </div>
              <div className="flex space-x-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() =>
                    document
                      .getElementById("roomsContainer")
                      ?.scrollBy({ left: -200, behavior: "smooth" })
                  }
                >
                  &larr;
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() =>
                    document
                      .getElementById("roomsContainer")
                      ?.scrollBy({ left: 200, behavior: "smooth" })
                  }
                >
                  &rarr;
                </Button>
              </div>
            </div>
          </CardHeader>
          <CardContent>
            <div
              id="roomsContainer"
              className="flex overflow-x-auto pb-4 space-x-4 scrollbar-hide"
            >
              {rooms.map((room, index) => (
                <div
                  key={index}
                  className={`flex-shrink-0 w-32 h-32 rounded-lg p-4 flex flex-col items-center justify-center border ${
                    room.status === "available"
                      ? "bg-green-100 dark:bg-green-900/30 border-green-200 dark:border-green-800"
                      : room.status === "occupied"
                      ? "bg-red-100 dark:bg-red-900/30 border-red-200 dark:border-red-800"
                      : "bg-yellow-100 dark:bg-yellow-900/30 border-yellow-200 dark:border-yellow-800"
                  }`}
                >
                  <div className="text-2xl font-bold mb-1">{room.number}</div>
                  <div className="text-xs text-gray-500 dark:text-gray-400 mb-2">
                    {room.type}
                  </div>
                  <span
                    className={`px-2 py-1 text-xs rounded-full ${
                      room.status === "available"
                        ? "bg-green-200 text-green-800 dark:bg-green-800 dark:text-green-200"
                        : room.status === "occupied"
                        ? "bg-red-200 text-red-800 dark:bg-red-800 dark:text-red-200"
                        : "bg-yellow-200 text-yellow-800 dark:bg-yellow-800 dark:text-yellow-200"
                    }`}
                  >
                    {room.status}
                  </span>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Notifications Section */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        <Card className="lg:col-span-2 border-0 bg-white/80 dark:bg-slate-800/80 backdrop-blur-sm hover:shadow-xl transition-all duration-300">
          <CardHeader className="pb-4">
            <CardTitle className="text-xl font-semibold">
              Recent Activity
            </CardTitle>
            <CardDescription className="text-base">
              Latest hotel activities and updates
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {notifications.map((notification) => (
                <div
                  key={notification.id}
                  className="flex items-start space-x-4 p-4 rounded-xl border border-gray-100 dark:border-gray-700 hover:bg-gray-50 dark:hover:bg-slate-700/50 transition-all duration-200 group"
                >
                  <div className="flex-shrink-0 p-2 rounded-lg bg-blue-100 dark:bg-blue-900/30 group-hover:bg-blue-200 dark:group-hover:bg-blue-900/50 transition-colors duration-200">
                    {getNotificationIcon(notification.type)}
                  </div>
                  <div className="flex-1 min-w-0">
                    <p className="text-sm font-medium text-foreground leading-relaxed">
                      {notification.message}
                    </p>
                    <div className="flex items-center space-x-3 mt-2">
                      <div className="flex items-center space-x-1">
                        <Clock className="h-3 w-3 text-muted-foreground" />
                        <span className="text-xs text-muted-foreground">
                          {notification.time}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Quick Actions */}
        <Card className="border-0 bg-white/80 dark:bg-slate-800/80 backdrop-blur-sm hover:shadow-xl transition-all duration-300">
          <CardHeader className="pb-4">
            <CardTitle className="text-xl font-semibold">
              Quick Actions
            </CardTitle>
            <CardDescription className="text-base">
              Common tasks and shortcuts
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <Button
              className="w-full justify-start h-12 text-base font-medium hover:bg-blue-50 hover:border-blue-200 transition-all duration-200 group"
              variant="outline"
              onClick={() => {
                navigate("/guests");
                const event = new CustomEvent("showToast", {
                  detail: {
                    type: "info",
                    title: "Add New Guest",
                    message:
                      "Opening Guest Management with Add Guest form...",
                  },
                });
                window.dispatchEvent(event);
              }}
            >
              <div className="p-2 rounded-lg bg-blue-100 dark:bg-blue-900/30 group-hover:bg-blue-200 dark:group-hover:bg-blue-900/50 transition-colors duration-200 mr-3">
                <Users className="h-5 w-5 text-blue-600 dark:text-blue-400" />
              </div>
              Add New Guest
            </Button>

            <Button
              className="w-full justify-start h-12 text-base font-medium hover:bg-green-50 hover:border-green-200 transition-all duration-200 group"
              variant="outline"
              onClick={() => {
                navigate("/rooms");
                const event = new CustomEvent("showToast", {
                  detail: {
                    type: "info",
                    title: "Room Management",
                    message: "Redirecting to Room Management...",
                  },
                });
                window.dispatchEvent(event);
              }}
            >
              <div className="p-2 rounded-lg bg-green-100 dark:bg-green-900/30 group-hover:bg-green-200 dark:group-hover:bg-green-900/50 transition-colors duration-200 mr-3">
                <Bed className="h-5 w-5 text-green-600 dark:text-green-400" />
              </div>
              Assign Room
            </Button>

            <Button
              className="w-full justify-start h-12 text-base font-medium hover:bg-purple-50 hover:border-purple-200 transition-all duration-200 group"
              variant="outline"
              onClick={() => {
                navigate("/notifications");
                const event = new CustomEvent("showToast", {
                  detail: {
                    type: "info",
                    title: "Notifications",
                    message: "Redirecting to Notifications...",
                  },
                });
                window.dispatchEvent(event);
              }}
            >
              <div className="p-2 rounded-lg bg-purple-100 dark:bg-purple-900/30 group-hover:bg-purple-200 dark:group-hover:bg-purple-900/50 transition-colors duration-200 mr-3">
                <Bell className="h-5 w-5 text-purple-600 dark:text-purple-400" />
              </div>
              Send Notification
            </Button>
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default Dashboard;
