import React, { useState } from 'react';
import { User } from '../App';
import { Card, CardHeader, CardTitle, CardDescription, CardContent } from './ui/card';
import { Button } from './ui/button';
import { UserPlus, Edit, Trash2, Shield, Info } from 'lucide-react';

const roleColors: Record<string, string> = {
  Admin: 'bg-red-100 text-red-800',
  Reception: 'bg-blue-100 text-blue-800',
  Housekeeping: 'bg-green-100 text-green-800',
};

const Users: React.FC = () => {
  const [users, setUsers] = useState<User[]>([
    {
      username: 'admin',
      email: 'admin@hotel.com',
      role: 'Admin',
      accessScope: 'All',
    },
    {
      username: 'reception01',
      email: 'reception@hotel.com',
      role: 'Reception',
      accessScope: 'All',
    },
    {
      username: 'housekeeping_f2',
      email: 'housekeeping.f2@hotel.com',
      role: 'Housekeeping',
      accessScope: 'Floor 2 only',
    },
  ]);

  const getRoleBadge = (role: string) => (
    <span className={`px-3 py-1 rounded-full text-xs font-semibold ${roleColors[role] || 'bg-gray-100 text-gray-800'}`}>{role}</span>
  );

  const handleEditUser = (user: User) => {
    // In a real app, this would open an edit modal or navigate to edit page
    console.log('Editing user:', user);
  };

  const handleDeleteUser = (username: string) => {
    // In a real app, this would show a confirmation dialog
    setUsers(users.filter(user => user.username !== username));
  };

  const handleAddUser = () => {
    // In a real app, this would open an add user modal or navigate to add page
    console.log('Adding new user...');
  };

  return (
    <div className="max-w-5xl mx-auto">
      <Card className="mb-8">
        <CardHeader>
          <CardTitle>User Management</CardTitle>
          <CardDescription>
            Manage system access, roles, and permissions for hotel staff.
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4 mb-6">
            <div className="text-sm text-gray-500 flex items-center gap-2">
              <Shield className="h-4 w-4 text-blue-400" />
              Role-based feature visibility. Login history and account lockout available. Admins can promote/demote users.
            </div>
            <Button onClick={handleAddUser} className="flex items-center gap-2" variant="outline">
              <UserPlus className="h-5 w-5" /> Add User
            </Button>
          </div>
          <div className="overflow-x-auto rounded-lg border border-gray-200 dark:border-gray-700 bg-white dark:bg-slate-800">
            <table className="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
              <thead className="bg-gray-50 dark:bg-slate-700/50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Username</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Email</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Role</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Access Scope</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Actions</th>
                </tr>
              </thead>
              <tbody className="bg-white dark:bg-slate-800 divide-y divide-gray-200 dark:divide-gray-700">
                {users.map((user, index) => (
                  <tr key={index}>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 dark:text-white">{user.username}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300">{user.email}</td>
                    <td className="px-6 py-4 whitespace-nowrap">{getRoleBadge(user.role)}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300">{user.accessScope}</td>
                    <td className="px-6 py-4 whitespace-nowrap flex gap-2">
                      <Button size="icon" variant="ghost" onClick={() => handleEditUser(user)} title="Edit user">
                        <Edit className="h-5 w-5 text-blue-500" />
                      </Button>
                      <Button size="icon" variant="ghost" onClick={() => handleDeleteUser(user.username)} title="Delete user">
                        <Trash2 className="h-5 w-5 text-red-500" />
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </CardContent>
      </Card>
      <Card className="mb-4">
        <CardHeader>
          <CardTitle className="flex items-center gap-2 text-base">
            <Info className="h-5 w-5 text-blue-400" /> User Management Notes
          </CardTitle>
        </CardHeader>
        <CardContent>
         
        </CardContent>
      </Card>
    </div>
  );
};

export default Users;
