import React, { useState } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './ui/card';
import { Badge } from './ui/badge';
import { Button } from './ui/button';
import { 
  Bed, 
  Users, 
  Settings, 
  Plus, 
  Grid, 
  List, 
  Edit, 
  Trash2, 
  Eye,
  Wifi,
  Coffee,
  Car,
  Dumbbell,
  Eye as EyeIcon
} from 'lucide-react';
import { HotelDashboard as RoomDetailsModal } from './RoomDetailsModal';

interface Room {
  id: string;
  number: string;
  type: string;
  floor: number;
  status: 'available' | 'occupied' | 'maintenance' | 'cleaning';
  guestName?: string;
  checkIn?: string;
  checkOut?: string;

  amenities: string[];
  lastUpdated: string;
}

const RoomsManagement = () => {
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
  const [filterStatus, setFilterStatus] = useState<string>('all');
  const [isAddRoomModalOpen, setIsAddRoomModalOpen] = useState(false);
  const [selectedRoom, setSelectedRoom] = useState<Room | null>(null);
  const [isDetailsModalOpen, setIsDetailsModalOpen] = useState(false);
  const [newRoom, setNewRoom] = useState({
    number: '',
    type: 'Standard',
    floor: 1,
    status: 'available' as const,
    amenities: [] as string[],
    userId: '',
    password: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleAddRoom = (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);
    // Here you would typically make an API call to add the room
    console.log('Adding new room:', newRoom);

    // Simulate API call
    setTimeout(() => {
      setIsSubmitting(false);
      setIsAddRoomModalOpen(false);
      // Reset form
      setNewRoom({
        number: '',
        type: 'Standard',
        floor: 1,
        status: 'available',
        amenities: [],
        userId: '',
        password: ''
      });

      // Show success toast
      window.dispatchEvent(new CustomEvent('showToast', {
        detail: {
          type: 'success',
          title: 'Room Added',
          message: 'New room has been added successfully!'
        }
      }));
    }, 1000);
  };

  const handleAmenityToggle = (amenity: string) => {
    setNewRoom(prev => ({
      ...prev,
      amenities: prev.amenities.includes(amenity)
        ? prev.amenities.filter(a => a !== amenity)
        : [...prev.amenities, amenity]
    }));
  };

  const handleViewDetails = (room: Room) => {
    setSelectedRoom(room);
    setIsDetailsModalOpen(true);
  };

  const rooms: Room[] = [
    {
      id: '1',
      number: '101',
      type: 'Standard',
      floor: 1,
      status: 'occupied',
      guestName: 'John Smith',
      checkIn: '2024-01-15',
      checkOut: '2024-01-18',

      amenities: ['wifi', 'coffee'],
      lastUpdated: '2 hours ago'
    },
    {
      id: '2',
      number: '102',
      type: 'Deluxe',
      floor: 1,
      status: 'available',

      amenities: ['wifi', 'coffee', 'parking'],
      lastUpdated: '1 hour ago'
    },
    {
      id: '3',
      number: '201',
      type: 'Suite',
      floor: 2,
      status: 'maintenance',

      amenities: ['wifi', 'coffee', 'parking', 'gym'],
      lastUpdated: '30 minutes ago'
    },
    {
      id: '4',
      number: '202',
      type: 'Standard',
      floor: 2,
      status: 'cleaning',
      amenities: ['wifi'],
      lastUpdated: '15 minutes ago'
    },
    {
      id: '5',
      number: '301',
      type: 'Presidential',
      floor: 3,
      status: 'occupied',
      guestName: 'Sarah Johnson',
      checkIn: '2024-01-14',
      checkOut: '2024-01-20',

      amenities: ['wifi', 'coffee', 'parking', 'gym', 'concierge'],
      lastUpdated: '1 hour ago'
    },
    {
      id: '6',
      number: '302',
      type: 'Deluxe',
      floor: 3,
      status: 'available',

      amenities: ['wifi', 'coffee', 'parking'],
      lastUpdated: '45 minutes ago'
    }
  ];

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'available':
        return <Badge variant="success">Available</Badge>;
      case 'occupied':
        return <Badge variant="info">Occupied</Badge>;
      case 'maintenance':
        return <Badge variant="destructive">Maintenance</Badge>;
      case 'cleaning':
        return <Badge variant="warning">Cleaning</Badge>;
      default:
        return <Badge variant="default">{status}</Badge>;
    }
  };

  const getAmenityIcon = (amenity: string) => {
    const iconClass = "h-3 w-3 sm:h-3.5 sm:w-3.5 flex-shrink-0";
    switch (amenity) {
      case 'wifi': return <Wifi className={iconClass} />;
      case 'coffee': return <Coffee className={iconClass} />;
      case 'parking': return <Car className={iconClass} />;
      case 'gym': return <Dumbbell className={iconClass} />;
      default: return null;
    }
  };

  const filteredRooms = rooms.filter(room => 
    filterStatus === 'all' || room.status === filterStatus
  );

  const RoomCard = ({ room }: { room: Room }) => (
    <Card className="hover:shadow-md transition-shadow h-full flex flex-col">
      <CardHeader className="pb-3 px-4 sm:px-6 pt-4">
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-2">
          <div className="space-y-1">
            <div className="flex items-center justify-between">
              <CardTitle className="text-base sm:text-lg">Room {room.number}</CardTitle>
              <div className="sm:hidden">
                {getStatusBadge(room.status)}
              </div>
            </div>
            <CardDescription className="text-xs sm:text-sm">
              Floor {room.floor} • {room.type}
            </CardDescription>
          </div>
          <div className="hidden sm:block">
            {getStatusBadge(room.status)}
          </div>
        </div>
      </CardHeader>
      <CardContent className="px-4 sm:px-6 pb-4 pt-0 flex-1 flex flex-col">
        <div className="space-y-2 flex-1">
          {room.guestName && (
            <div className="flex items-center space-x-2">
              <Users className="h-3.5 w-3.5 sm:h-4 sm:w-4 text-muted-foreground flex-shrink-0" />
              <span className="text-xs sm:text-sm font-medium truncate">{room.guestName}</span>
            </div>
          )}

          {room.amenities.length > 0 && (
            <div className="flex flex-wrap gap-2 py-1">
              {room.amenities.slice(0, 3).map((amenity) => (
                <span key={amenity} className="inline-flex items-center text-xs px-2 py-0.5 rounded-full bg-muted text-muted-foreground">
                  {getAmenityIcon(amenity)}
                  <span className="ml-1 hidden xs:inline">{amenity}</span>
                </span>
              ))}
              {room.amenities.length > 3 && (
                <span className="text-xs text-muted-foreground">+{room.amenities.length - 3} more</span>
              )}
            </div>
          )}
        </div>

        <div className="flex items-center justify-between pt-3 mt-auto border-t">
          <span className="text-xs text-muted-foreground truncate pr-2">
            Updated {room.lastUpdated}
          </span>
          <div className="flex space-x-1 flex-shrink-0">
            <Button 
              variant="ghost" 
              size="icon"
              onClick={() => handleViewDetails(room)}
            >
              <EyeIcon className="h-4 w-4" />
            </Button>
            <Button variant="ghost" size="icon" className="h-8 w-8">
              <Edit className="h-3.5 w-3.5" />
            </Button>
            <Button variant="ghost" size="icon" className="h-8 w-8">
              <Trash2 className="h-3.5 w-3.5" />
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );

  const RoomListItem = ({ room }: { room: Room }) => (
    <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between p-3 sm:p-4 border rounded-lg hover:bg-muted/50 transition-colors">
      <div className="flex items-start sm:items-center space-x-3 sm:space-x-4 w-full sm:w-auto">
        <div className="w-10 h-10 sm:w-12 sm:h-12 bg-primary/10 rounded-lg flex items-center justify-center flex-shrink-0">
          <Bed className="h-5 w-5 sm:h-6 sm:w-6 text-primary" />
        </div>
        <div className="flex-1 min-w-0">
          <div className="flex items-center justify-between">
            <h3 className="font-semibold text-sm sm:text-base truncate">Room {room.number}</h3>
            <div className="sm:hidden ml-2">
              {getStatusBadge(room.status)}
            </div>
          </div>
          <p className="text-xs sm:text-sm text-muted-foreground truncate">
            {room.type} • Floor {room.floor}
          </p>
          {room.guestName && (
            <p className="text-xs text-muted-foreground truncate mt-0.5">
              Occupied by {room.guestName}
            </p>
          )}
          <div className="flex flex-wrap gap-1.5 mt-1.5">
            {room.amenities.slice(0, 2).map((amenity) => (
              <span key={amenity} className="inline-flex items-center text-[10px] sm:text-xs px-1.5 py-0.5 rounded-full bg-muted text-muted-foreground">
                {getAmenityIcon(amenity)}
                <span className="ml-0.5">{amenity}</span>
              </span>
            ))}
            {room.amenities.length > 2 && (
              <span className="text-[10px] sm:text-xs text-muted-foreground">
                +{room.amenities.length - 2} more
              </span>
            )}
          </div>
        </div>
      </div>

      <div className="flex items-center space-x-4 mt-2 sm:mt-0">
        <div className="hidden sm:block">
          {getStatusBadge(room.status)}
        </div>

        <div className="flex space-x-1">
          <Button 
            variant="ghost" 
            size="sm"
            onClick={() => handleViewDetails(room)}
          >
            <EyeIcon className="h-4 w-4" />
          </Button>
          <Button 
            variant="ghost" 
            size="sm"
            onClick={() => {
              const event = new CustomEvent('showToast', {
                detail: { type: 'info', title: 'Edit Room', message: `Editing Room ${room.number}` }
              });
              window.dispatchEvent(event);
            }}
          >
            <Edit className="h-4 w-4" />
          </Button>
          <Button 
            variant="ghost" 
            size="sm"
            onClick={() => {
              const event = new CustomEvent('showToast', {
                detail: { type: 'warning', title: 'Delete Room', message: `Are you sure you want to delete Room ${room.number}?` }
              });
              window.dispatchEvent(event);
            }}
          >
            <Trash2 className="h-4 w-4" />
          </Button>
        </div>
      </div>
    </div>
  );

  const FilterAndViewControls = () => {
    return (
      <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4 mb-4">
        <div className="flex flex-wrap items-center gap-2 w-full sm:w-auto">
        <Button 
          variant={viewMode === 'grid' ? 'default' : 'outline'} 
          size="sm" 
          onClick={() => setViewMode('grid')}
          className="h-8"
        >
          <Grid className="h-4 w-4 mr-2" />
          Grid
        </Button>
        <Button 
          variant={viewMode === 'list' ? 'default' : 'outline'} 
          size="sm" 
          onClick={() => setViewMode('list')}
          className="h-8"
        >
          <List className="h-4 w-4 mr-2" />
          List
        </Button>
        <select
          value={filterStatus}
          onChange={(e) => setFilterStatus(e.target.value)}
          className="h-8 rounded-md border border-input bg-background px-3 py-1 text-sm shadow-sm transition-colors focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring"
        >
          <option value="all">All Status</option>
          <option value="available">Available</option>
          <option value="occupied">Occupied</option>
          <option value="maintenance">Maintenance</option>
          <option value="cleaning">Cleaning</option>
        </select>
      </div>

        <div className="text-sm text-muted-foreground w-full sm:w-auto text-center sm:text-right">
          {filteredRooms.length} {filteredRooms.length === 1 ? 'room' : 'rooms'} found
        </div>
      </div>
    );
  };

  return (
    <div className="space-y-3 md:space-y-4 p-2.5 sm:p-4 max-w-[100vw] overflow-x-hidden">
      <FilterAndViewControls />

      {/* Add Room Modal */}
      {isAddRoomModalOpen && (
        <div className="fixed inset-0 bg-black/50 flex items-start sm:items-center justify-center z-50 p-0 sm:p-4 overflow-y-auto">
          <div className="bg-white dark:bg-slate-800 rounded-lg shadow-xl w-full h-full sm:h-auto sm:max-h-[90vh] sm:max-w-md sm:mx-auto sm:my-8 p-4 sm:p-6 overflow-y-auto">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-semibold">Add New Room</h3>
              <button 
                onClick={() => setIsAddRoomModalOpen(false)}
                className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-200"
              >
                ✕
              </button>
            </div>

            {/* Form */}
            <form onSubmit={handleAddRoom} className="space-y-4">
              <div className="grid gap-4">
                {/* Room Number */}
                <div className="space-y-2">
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                    Room Number *
                  </label>
                  <input
                    type="text"
                    required
                    value={newRoom.number}
                    onChange={(e) => setNewRoom({...newRoom, number: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 dark:bg-slate-700 dark:text-white"
                    placeholder="e.g., 101"
                  />
                </div>

                {/* User ID */}
                <div className="space-y-2">
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                    Room ID *
                  </label>
                  <input
                    type="text"
                    required
                    value={newRoom.userId || ''}
                    onChange={(e) => setNewRoom({...newRoom, userId: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 dark:bg-slate-700 dark:text-white"
                    placeholder="Enter user ID"
                  />
                </div>

                {/* Password */}
                <div className="space-y-2">
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                    Password *
                  </label>
                  <input
                    type="password"
                    required
                    value={newRoom.password || ''}
                    onChange={(e) => setNewRoom({...newRoom, password: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 dark:bg-slate-700 dark:text-white"
                    placeholder="Create a password"
                  />
                </div>
              </div>

              {/* Form Actions */}
              <div className="flex justify-end gap-3 pt-4">
                <button
                  type="button"
                  onClick={() => setIsAddRoomModalOpen(false)}
                  className="px-4 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 bg-gray-100 dark:bg-slate-700 rounded-md hover:bg-gray-200 dark:hover:bg-slate-600 focus:outline-none"
                  disabled={isSubmitting}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 text-sm font-medium text-white bg-blue-600 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 flex items-center justify-center min-w-[100px]"
                  disabled={isSubmitting}
                >
                  {isSubmitting ? (
                    <>
                      <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                      </svg>
                      Adding...
                    </>
                  ) : 'Add Room'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Mobile filter */}
      <div className="mb-3 sm:hidden">
        <select
          value={filterStatus}
          onChange={(e) => setFilterStatus(e.target.value)}
          className="w-full text-sm p-2 border rounded-md bg-background"
        >
          <option value="all">All Status</option>
          <option value="available">Available</option>
          <option value="occupied">Occupied</option>
          <option value="maintenance">Maintenance</option>
          <option value="cleaning">Cleaning</option>
        </select>
      </div>

      {/* Room list/grid */}
      {viewMode === "grid" ? (
        <div className="grid grid-cols-1 xs:grid-cols-2 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-2.5 sm:gap-3 md:gap-4">
          {filteredRooms.map((room) => (
            <RoomCard key={room.id} room={room} />
          ))}
        </div>
      ) : (
        <div className="space-y-2 sm:space-y-3">
          {filteredRooms.map((room) => (
            <RoomListItem key={room.id} room={room} />
          ))}
        </div>
      )}

      {/* Floating Action Button */}
      <Button 
        className="fixed bottom-4 right-4 sm:bottom-6 sm:right-6 h-14 w-14 rounded-full shadow-lg text-lg z-40"
        onClick={() => setIsAddRoomModalOpen(true)}
        size="icon"
      >
        <Plus className="h-5 w-5 sm:h-6 sm:w-6" />
      </Button>

      {/* Room Details Modal */}
      {selectedRoom && (
        <RoomDetailsModal
          isOpen={isDetailsModalOpen}
          onClose={() => setIsDetailsModalOpen(false)}
          room={selectedRoom}
        />
      )}
    </div>
  );

};


export default RoomsManagement;
