import React, { useState } from 'react';
import { User } from '../App';

const Users: React.FC = () => {
  const [users, setUsers] = useState<User[]>([
    {
      username: 'admin',
      email: 'admin@hotel.com',
      role: 'Admin',
      accessScope: 'All',
    },
    {
      username: 'reception01',
      email: 'reception@hotel.com',
      role: 'Reception',
      accessScope: 'All',
    },
    {
      username: 'housekeeping_f2',
      email: 'housekeeping.f2@hotel.com',
      role: 'Housekeeping',
      accessScope: 'Floor 2 only',
    },
  ]);

  const getRoleBadge = (role: string) => {
    switch (role) {
      case 'Admin':
        return <span className="badge badge-red">{role}</span>;
      case 'Reception':
        return <span className="badge badge-blue">{role}</span>;
      case 'Housekeeping':
        return <span className="badge badge-green">{role}</span>;
      default:
        return <span className="badge badge-gray">{role}</span>;
    }
  };

  const handleEditUser = (user: User) => {
    // In a real app, this would open an edit modal or navigate to edit page
    console.log('Editing user:', user);
  };

  const handleDeleteUser = (username: string) => {
    // In a real app, this would show a confirmation dialog
    setUsers(users.filter(user => user.username !== username));
  };

  const handleAddUser = () => {
    // In a real app, this would open an add user modal or navigate to add page
    console.log('Adding new user...');
  };

  return (
    <div>
      <div className="flex justify-end mb-6">
        <button className="btn btn-primary" onClick={handleAddUser}>
          <i className="fas fa-plus"></i> Add New User
        </button>
      </div>
      <div className="overflow-x-auto border border-gray-200 rounded-lg">
        <table className="table">
          <thead>
            <tr>
              <th>Username</th>
              <th>Email</th>
              <th>Role</th>
              <th>Access Scope</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user, index) => (
              <tr key={index}>
                <td>{user.username}</td>
                <td>{user.email}</td>
                <td>{getRoleBadge(user.role)}</td>
                <td>{user.accessScope}</td>
                <td className="flex gap-2">
                  <button
                    className="text-blue-600 hover:text-blue-800"
                    onClick={() => handleEditUser(user)}
                    title="Edit user"
                  >
                    <i className="fas fa-edit"></i>
                  </button>
                  <button
                    className="text-red-600 hover:text-red-800"
                    onClick={() => handleDeleteUser(user.username)}
                    title="Delete user"
                  >
                    <i className="fas fa-trash"></i>
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Users;
