import React from "react";
import {
  X,
  Phone,
  Mail,
  Edit,
  Bed,
  Users,
  Heart,
  CheckCircle2,
  Clock,
  Calendar,
  CreditCard,
  Info,
} from "lucide-react";

interface RoomDetailsModalProps {
  isOpen: boolean;
  onClose: () => void;
  room: {
    id: string;
    number: string;
    type: string;
    floor: number;
    status: "available" | "occupied" | "maintenance";
    price: number;
    capacity: number;
    guestName?: string;
    guestEmail?: string;
    guestPhone?: string;
    checkIn?: string;
    checkOut?: string;
    specialRequests?: string;
  };
}

const statusColors: Record<string, string> = {
  available: "bg-green-100 text-green-700",
  occupied: "bg-red-100 text-red-700",
  maintenance: "bg-yellow-100 text-yellow-700",
};

const RoomDetailsModal: React.FC<RoomDetailsModalProps> = ({ isOpen, onClose, room }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/50 p-4">
      <div className="relative w-full max-w-lg rounded-2xl bg-white shadow-xl">
        {/* Header */}
        <div className="flex items-center justify-between border-b p-4">
          <h2 className="text-xl font-semibold flex items-center gap-2">
            <Bed className="h-5 w-5" /> Room {room.number}
          </h2>
          <button onClick={onClose} className="p-2 rounded-full hover:bg-gray-100">
            <X className="h-5 w-5" />
          </button>
        </div>

        {/* Content */}
        <div className="p-4 space-y-4">
          {/* Status */}
          <div className="flex items-center gap-2">
            <CheckCircle2 className="h-5 w-5 text-gray-500" />
            <span
              className={`px-3 py-1 rounded-full text-sm font-medium ${statusColors[room.status]}`}
            >
              {room.status.toUpperCase()}
            </span>
          </div>

          {/* Room Info */}
          <div className="grid grid-cols-2 gap-4">
            <div className="flex items-center gap-2">
              <Users className="h-5 w-5 text-gray-500" />
              Capacity: {room.capacity}
            </div>
            <div className="flex items-center gap-2">
              <CreditCard className="h-5 w-5 text-gray-500" />
              ₹{room.price}/night
            </div>
            <div className="flex items-center gap-2">
              <Info className="h-5 w-5 text-gray-500" />
              {room.type}
            </div>
            <div className="flex items-center gap-2">
              <Heart className="h-5 w-5 text-gray-500" />
              Floor {room.floor}
            </div>
          </div>

          {/* Guest Info */}
          {room.guestName && (
            <div className="rounded-lg border p-3 bg-gray-50 space-y-2">
              <h3 className="font-semibold">Guest Details</h3>
              <div className="flex items-center gap-2 text-sm">
                <Users className="h-4 w-4 text-gray-500" /> {room.guestName}
              </div>
              {room.guestEmail && (
                <div className="flex items-center gap-2 text-sm">
                  <Mail className="h-4 w-4 text-gray-500" /> {room.guestEmail}
                </div>
              )}
              {room.guestPhone && (
                <div className="flex items-center gap-2 text-sm">
                  <Phone className="h-4 w-4 text-gray-500" /> {room.guestPhone}
                </div>
              )}
            </div>
          )}

          {/* Booking Info */}
          {(room.checkIn || room.checkOut) && (
            <div className="rounded-lg border p-3 bg-gray-50 space-y-2">
              <h3 className="font-semibold">Booking Info</h3>
              {room.checkIn && (
                <div className="flex items-center gap-2 text-sm">
                  <Calendar className="h-4 w-4 text-gray-500" /> Check-in: {room.checkIn}
                </div>
              )}
              {room.checkOut && (
                <div className="flex items-center gap-2 text-sm">
                  <Clock className="h-4 w-4 text-gray-500" /> Check-out: {room.checkOut}
                </div>
              )}
            </div>
          )}

          {/* Special Requests */}
          {room.specialRequests && (
            <div className="rounded-lg border p-3 bg-gray-50">
              <h3 className="font-semibold mb-1">Special Requests</h3>
              <p className="text-sm text-gray-700">{room.specialRequests}</p>
            </div>
          )}
        </div>

        {/* Footer */}
        <div className="flex justify-end gap-2 border-t p-4">
          <button
            onClick={onClose}
            className="px-4 py-2 rounded-lg bg-gray-100 hover:bg-gray-200"
          >
            Close
          </button>
          <button className="px-4 py-2 rounded-lg bg-blue-600 text-white hover:bg-blue-700 flex items-center gap-2">
            <Edit className="h-4 w-4" /> Edit
          </button>
        </div>
      </div>
    </div>
  );
};

export default RoomDetailsModal;
