// Remove unused useState import
import { useNavigate } from 'react-router-dom';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './ui/card';
import { Badge } from './ui/badge';
import { Button } from './ui/button';
import { 
  Users, 
  Bed, 
  Bell,
  DollarSign, 
  TrendingUp, 
  TrendingDown,
 
  Calendar,
  Clock
} from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';
import { formatCurrency } from '../lib/utils';

const Dashboard = () => {
  const navigate = useNavigate();


  // Mock data for charts
  const occupancyData = [
    { name: 'Mon', occupancy: 85 },
    { name: 'Tue', occupancy: 92 },
    { name: 'Wed', occupancy: 78 },
    { name: 'Thu', occupancy: 95 },
    { name: 'Fri', occupancy: 88 },
    { name: 'Sat', occupancy: 96 },
    { name: 'Sun', occupancy: 82 },
  ];

  const roomTypeData = [
    { name: 'Standard', value: 45, color: '#3b82f6' },
    { name: 'Deluxe', value: 30, color: '#10b981' },
    { name: 'Suite', value: 15, color: '#f59e0b' },
    { name: 'Presidential', value: 10, color: '#ef4444' },
  ];

  const notifications = [
    {
      id: 1,
      type: 'checkin',
      message: 'New guest checked in to Room 205',
      time: '2 minutes ago',
      priority: 'low'
    },
    {
      id: 2,
      type: 'maintenance',
      message: 'Maintenance request for Room 312',
      time: '15 minutes ago',
      priority: 'high'
    },
    {
      id: 3,
      type: 'checkout',
      message: 'Guest checked out from Room 108',
      time: '1 hour ago',
      priority: 'low'
    },
    {
      id: 4,
      type: 'review',
      message: 'New 5-star review received',
      time: '2 hours ago',
      priority: 'medium'
    }
  ];

  const stats = [
    {
      title: 'Total Rooms',
      value: '150',
     
      icon: Bed,
      
      gradient: 'from-blue-500 to-blue-600'
    },
    {
      title: 'Occupied Rooms',
      value: '127',
      
      icon: Users,
     
      gradient: 'from-green-500 to-green-600'
    },
    {
      title: 'Available Rooms',
      value: '23',
      
      
     
     
      gradient: 'from-purple-500 to-purple-600'
    },
    
    {
      title: 'Guests',
      value: '89',
      
     
      
      
      gradient: 'from-pink-500 to-pink-600'
    }
  ];

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high': return 'destructive';
      case 'medium': return 'warning';
      case 'low': return 'success';
      default: return 'default';
    }
  };

  const getNotificationIcon = (type: string) => {
    switch (type) {
      case 'checkin': return <Users className="h-4 w-4" />;
      case 'checkout': return <Users className="h-4 w-4" />;
      case 'maintenance': return <Bed className="h-4 w-4" />;
      
      
    }
  };

  return (
    <div className="space-y-8 p-6 bg-gradient-to-br from-slate-50 to-slate-100 dark:from-slate-900 dark:to-slate-800 min-h-screen">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-4xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
            Dashboard
          </h2>
          <p className="text-muted-foreground mt-2 text-lg">Welcome back! Here's what's happening today.</p>
        </div>
        <div className="flex items-center space-x-3">
          {/* <Button variant="outline" size="sm" className="hover:bg-blue-50 hover:border-blue-200 transition-all duration-200">
            Export Report
          </Button> */}
          <Button size="sm" className="bg-gradient-to-r from-blue-500 to-purple-500 hover:from-blue-600 hover:to-purple-600 transition-all duration-200">
            View Details
          </Button>
        </div>
      </div>

     

      {/* Charts Section */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        
      </div>

      {/* Notifications Section */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        <Card className="lg:col-span-2 border-0 bg-white/80 dark:bg-slate-800/80 backdrop-blur-sm hover:shadow-xl transition-all duration-300">
          <CardHeader className="pb-4">
            <CardTitle className="text-xl font-semibold">Recent Activity</CardTitle>
            <CardDescription className="text-base">Latest hotel activities and updates</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {notifications.map((notification) => (
                <div key={notification.id} className="flex items-start space-x-4 p-4 rounded-xl border border-gray-100 dark:border-gray-700 hover:bg-gray-50 dark:hover:bg-slate-700/50 transition-all duration-200 group">
                  <div className="flex-shrink-0 p-2 rounded-lg bg-blue-100 dark:bg-blue-900/30 group-hover:bg-blue-200 dark:group-hover:bg-blue-900/50 transition-colors duration-200">
                    {getNotificationIcon(notification.type)}
                  </div>
                  <div className="flex-1 min-w-0">
                    <p className="text-sm font-medium text-foreground leading-relaxed">
                      {notification.message}
                    </p>
                    <div className="flex items-center space-x-3 mt-2">
                      <div className="flex items-center space-x-1">
                        <Clock className="h-3 w-3 text-muted-foreground" />
                        <span className="text-xs text-muted-foreground">{notification.time}</span>
                      </div>
                      <Badge variant={getPriorityColor(notification.priority) as any} className="text-xs px-2 py-1">
                        {notification.priority}
                      </Badge>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Quick Actions */}
        <Card className="border-0 bg-white/80 dark:bg-slate-800/80 backdrop-blur-sm hover:shadow-xl transition-all duration-300">
          <CardHeader className="pb-4">
            <CardTitle className="text-xl font-semibold">Quick Actions</CardTitle>
            <CardDescription className="text-base">Common tasks and shortcuts</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <Button 
              className="w-full justify-start h-12 text-base font-medium hover:bg-blue-50 hover:border-blue-200 transition-all duration-200 group" 
              variant="outline"
              onClick={() => {
                navigate('/guests');
                const event = new CustomEvent('showToast', {
                  detail: { type: 'info', title: 'Add New Guest', message: 'Opening Guest Management with Add Guest form...' }
                });
                window.dispatchEvent(event);
              }}
            >
              <div className="p-2 rounded-lg bg-blue-100 dark:bg-blue-900/30 group-hover:bg-blue-200 dark:group-hover:bg-blue-900/50 transition-colors duration-200 mr-3">
                <Users className="h-5 w-5 text-blue-600 dark:text-blue-400" />
              </div>
              Add New Guest
            </Button>
            <Button 
              className="w-full justify-start h-12 text-base font-medium hover:bg-green-50 hover:border-green-200 transition-all duration-200 group" 
              variant="outline"
              onClick={() => {
                navigate('/rooms');
                const event = new CustomEvent('showToast', {
                  detail: { type: 'info', title: 'Room Management', message: 'Redirecting to Room Management...' }
                });
                window.dispatchEvent(event);
              }}
            >
              <div className="p-2 rounded-lg bg-green-100 dark:bg-green-900/30 group-hover:bg-green-200 dark:group-hover:bg-green-900/50 transition-colors duration-200 mr-3">
                <Bed className="h-5 w-5 text-green-600 dark:text-green-400" />
              </div>
              Assign Room
            </Button>
            <Button 
              className="w-full justify-start h-12 text-base font-medium hover:bg-purple-50 hover:border-purple-200 transition-all duration-200 group" 
              variant="outline"
              onClick={() => {
                navigate('/notifications');
                const event = new CustomEvent('showToast', {
                  detail: { type: 'info', title: 'Notifications', message: 'Redirecting to Notifications...' }
                });
                window.dispatchEvent(event);
              }}
            >
              <div className="p-2 rounded-lg bg-purple-100 dark:bg-purple-900/30 group-hover:bg-purple-200 dark:group-hover:bg-purple-900/50 transition-colors duration-200 mr-3">
                <Bell className="h-5 w-5 text-purple-600 dark:text-purple-400" />
              </div>
              Send Notification
            </Button>
            {/* <Button 
              className="w-full justify-start h-12 text-base font-medium hover:bg-orange-50 hover:border-orange-200 transition-all duration-200 group" 
              variant="outline"
              onClick={() => {
                navigate('/calendar');
                const event = new CustomEvent('showToast', {
                  detail: { type: 'info', title: 'Calendar View', message: 'Redirecting to Calendar...' }
                });
                window.dispatchEvent(event);
              }}
            >
              <div className="p-2 rounded-lg bg-orange-100 dark:bg-orange-900/30 group-hover:bg-orange-200 dark:group-hover:bg-orange-900/50 transition-colors duration-200 mr-3">
                <Calendar className="h-5 w-5 text-orange-600 dark:text-orange-400" />
              </div>
              View Calendar
            </Button> */}
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default Dashboard;
